// 가속/감속 효과를 위한 이징 함수
function easeInOutCubic(t) {
  return t < 0.5 ? 4 * t * t * t : 1 - Math.pow(-2 * t + 2, 3) / 2;
}

const MorphingAnimation = () => {
  const svgRef = useRef(null);
  const pathRefs = useRef([]); // 여러 개의 path 엘리먼트를 관리
  const animationFrameRef = useRef(null); // requestAnimationFrame ID 저장

  // pathRefs 배열을 초기화하는 useEffect (최초 1회 실행)
  useEffect(() => {
    const svg = svgRef.current;
    if (!svg || !beforePaths) return; // beforePaths가 로드되었는지 확인

    // 기존 path 초기화
    svg.innerHTML = "";
    pathRefs.current = [];

    // beforePaths 기준으로 path 엘리먼트 생성
    beforePaths.forEach((d) => {
      const el = document.createElementNS("http://www.w3.org/2000/svg", "path");
      el.setAttribute("d", d);
      el.setAttribute("stroke", "black"); // 이미지에 맞춰 black으로 변경
      el.setAttribute("stroke-width", "1.5");
      el.setAttribute("fill", "none");
      svg.appendChild(el);
      pathRefs.current.push(el);
    });
  }, []); // 빈 배열: 컴포넌트 마운트 시 1회 실행

  // 애니메이션 함수 (Promise를 반환하도록 수정)
  const animatePaths = useCallback((targetPaths, stagger) => {
    return new Promise((resolve) => {
      // 기존 애니메이션이 있다면 취소
      if (animationFrameRef.current) {
        cancelAnimationFrame(animationFrameRef.current);
      }

      const paths = pathRefs.current;
      if (!paths.length || !targetPaths) {
        resolve(); // 실행할 대상이 없으면 즉시 종료
        return;
      }

      // flubber 보간기(interpolator) 생성
      const interpolators = paths.map((path, i) => {
        return flubber.interpolate(path.getAttribute("d"), targetPaths[i]);
      });

      // ⚠️ 애니메이션 속도 조절 ⚠️
      const duration = 1000; // 애니메이션 지속 시간 (ms) - 100 -> 1000
      const staggerDelay = stagger; // 각 경로의 지연 시간 (ms)
      let startTime = null;

      function frame(timestamp) {
        if (!startTime) startTime = timestamp;
        const totalElapsedTime = timestamp - startTime;

        let allDone = true;

        paths.forEach((path, i) => {
          const pathDelay = i * staggerDelay;
          if (totalElapsedTime > pathDelay) {
            const pathElapsedTime = totalElapsedTime - pathDelay;
            let progress = pathElapsedTime / duration;
            if (progress > 1) progress = 1;

            const easedProgress = easeInOutCubic(progress);
            path.setAttribute("d", interpolators[i](easedProgress));

            if (progress < 1) allDone = false;
          } else {
            allDone = false;
          }
        });

        if (!allDone) {
          animationFrameRef.current = requestAnimationFrame(frame);
        } else {
          resolve(); // 애니메이션 완료 시 Promise 해결
        }
      }

      animationFrameRef.current = requestAnimationFrame(frame);
    });
  }, []); // 의존성 배열이 비어있으므로 함수는 재생성되지 않음

  // 무한 반복 애니메이션을 위한 useEffect
  useEffect(() => {
    let isMounted = true;

    // ⚠️ 대기 시간 및 순차 지연 조절 ⚠️
    const pauseDuration = 50; // 애니메이션 사이 대기 시간 (ms) - 1500 -> 1000
    const staggerAmount = 20; // 순차 지연 시간 (ms) - 10/5 -> 30

    const runLoop = async () => {
      // path가 초기화될 때까지 잠시 대기
      if (pathRefs.current.length === 0) {
        if (isMounted) setTimeout(runLoop, 50); // 50ms 후 재시도
        return;
      }

      while (isMounted) {
        // 1. afterPaths로 변경
        await animatePaths(afterPaths, staggerAmount);
        if (!isMounted) break;

        // 2. 대기
        await new Promise((res) => setTimeout(res, pauseDuration));
        if (!isMounted) break;

        // 3. beforePaths로 복귀
        await animatePaths(beforePaths, staggerAmount);
        if (!isMounted) break;

        // 4. 대기
        await new Promise((res) => setTimeout(res, pauseDuration));
      }
    };

    runLoop();

    // 컴포넌트 언마운트 시 루프 중지 및 애니메이션 프레임 정리
    return () => {
      isMounted = false;
      if (animationFrameRef.current) {
        cancelAnimationFrame(animationFrameRef.current);
      }
    };
  }, [animatePaths]); // animatePaths 함수가 생성된 후 실행

  return (
    <div
      style={{
        backgroundColor: "#004FFC", // 이미지와 유사한 파란색 배경
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        height: "100vh",
      }}
    >
      <svg
        ref={svgRef}
        id="morphArea"
        viewBox="0 0 1171 1080" // viewBox는 SVG 데이터에 맞게 조절 필요
        fill="none"
        xmlns="http://www.w3.org/2000/svg"
        style={{ width: "61vw", height: "auto" }}
      ></svg>
    </div>
  );
};

export default MorphingAnimation;
